}

function collectPreText(node) {
  return String(node.value)
}

function removeBidiControlCharacters(value) {
  var index = -1
  var length = value.length
  var result = ''

  while (++index < length) {
    if (isBidiControlCharacter(value.charCodeAt(index))) {
      continue
    }

    result += value.charAt(index)
