    case 'nobr':
      return 'nowrap'
    case 'pre':
      return props.wrap ? 'pre-wrap' : 'pre'
    case 'td':
    case 'th':
      return props.noWrap ? 'nowrap' : inherit
    case 'textarea':
      return 'pre-wrap'
    default:
      return inherit
  }
}

function isBidiControlCharacter(char) {
  switch (char) {
    case alm:
    case ltr:
    case rtl:
    case lre:
    case rle:
    case pdf:
    case lro:
    case rlo:
    case lri:
    case rli:
    case fsi:
    case pdi:
      return true
    default:
      return false
  }
