function cell(node) {
  return is(node, ['th', 'td'])
}

function row(node) {
  return is(node, ['tr'])
}

// See: <https://html.spec.whatwg.org/#the-css-user-agent-style-sheet-and-presentational-hints>
function blockOrCaption(node) {
  return is(node, [
    'caption', // `table-caption`
    // Page
    'html',
    'body',
    // Flow content
    'address',
    'blockquote',
    'center', // Legacy
    'dialog',
    'div',
    'figure',
    'figcaption',
    'footer',
    'form,',
    'header',
    'hr',
    'legend',
    'listing', // Legacy
    'main',
    'p',
    'plaintext', // Legacy
    'pre',
    'xmp', // Legacy
    // Sections and headings
    'article',
    'aside',
    'h1',
    'h2',
    'h3',
    'h4',
    'h5',
    'h6',
    'hgroup',
    'nav',
    'section',
    // Lists
    'dir', // Legacy
    'dd',
    'dl',
    'dt',
    'menu',
    'ol',
    'ul'
  ])
