    // List from: <https://html.spec.whatwg.org/#hidden-elements>
    is(node, [
      'datalist',
      'head',
      'noembed',
      'noframes',
      'rp',
      'script',
      'style',
      'template',
      'title',
      // Act as if we support scripting.
      'noscript'
