    child = clean[index]
    title = is(child, 'dt')

    if (title && breakpoint) {
      groups.push(group)
      group = {titles: [], definitions: []}
    }

    group[title ? 'titles' : 'definitions'].push(child)
    breakpoint = is(child, 'dd')
  }

  groups.push(group)

  // Create items.
  length = groups.length
  index = -1
