'use strict'

var repeat = require('repeat-string')
var is = require('hast-util-is-element')
var findAfter = require('unist-util-find-after')

module.exports = toText

// Methods.
var min = Math.min
var max = Math.max

// White space codes.
var tab = 0x9
var space = 0x20
var zeroWidthSpace = 0x200b

// Bidi control characters codes.
var alm = 0x61c
var ltr = 0x200e
var rtl = 0x200f
var lre = 0x202a
var rle = 0x202b
var pdf = 0x202c
var lro = 0x202d
var rlo = 0x202e
var lri = 0x2066
var rli = 0x2067
var fsi = 0x2068
var pdi = 0x2069

// Characters.
