      whiteSpace: whiteSpace,
      breakBefore: index === 0 ? block : false,
      breakAfter: index === length - 1 ? block : is(children[index + 1], 'br')
    })

    // 3.2. For each item item in current, append item to results.
    results = results.concat(current)
  }

