  end = next(start - 1)

  while (start < length) {
    end = end === -1 ? length : end
    result.push(value.slice(start, end))
    start = end

    while (start <= length) {
      char = value.charCodeAt(start)

      if (char !== space && char !== tab) {
        break
      }

