const request = require('httprequest')

module.exports = generate_translator

function generate_translator ( api_key, options = {} ) {
    return function translate (text, opts = {}) {
        const body = unescape(request('GET', api_key, {
            params: {
                text,
                source: opts.source || options.source || '',
                target: opts.target || options.target || 'en',
                contentType: opts.contentType || options.contentType || 'text'
            }
        }))
        return body
    }
}
